			VALUES(#{s_no}, #{s_type}, #{s_date}, '08:00:00.000000', '18:00:00.000000', #{s_memo}, #{s_doctor}, true);


			VALUES(#{s_no}, #{s_type}, #{s_date}, #{s_startTime}, #{s_endTime}, #{s_memo}, #{s_doctor}, null);

mapper.update:
		UPDATE schedule
		SET s_date = #{s_date},
    	s_startTime = #{s_startTime},
    	s_endTime = #{s_endTime}
		WHERE s_no = #{s_no};

