mapper.select:

	select p_name, p_no, p_phone, p_addr
	from patient
	where p_name=#{p_name} and substring(#{p_no},1,6);

select s_date, s_startTime, s_endTime, s_doctor, s_memo
from schedule
where s_patient=#{s_patient} and substring(#{s_patientIdNum},1,6);

select count(*) 
from patient
where p_no = #{p_no};
